// the engine
//set up the App logic, methods etc

import { Component } from 'react';
import View from "./View.js";
// bring in View from another file
import { randomNumber, randomInteger } from './components/maths.js';



class App extends Component {
//use class if there is a need to handle state
static DIFFICULTIES = {
	easy: {
		tries: 10,
		upper: 25
	},
	medium: {
		tries: 7,
		upper: 50,
	},
	hard: {
		tries: 5,
		upper: 100,
	}
}

	render() {
		return (
				<View
					guess={() => {
						this.guess();
					}}

					help={e => {
						this.help();
					}}
				/>
		);
	}

	guess(number = 1) {
		alert(number);
	}

	help() {
		alert("Help");
	}


}

export default App;
