// the engine
//set up the App logic, methods etc

import { Component } from 'react';
import View from "./View.js";
// bring in View from another file
import { randomNumber, randomInteger } from './components/maths.js';



class App extends Component {
//use class if there is a need to handle state
	static DIFFICULTIES = {
		easy: {
			tries: 10,
			upper: 25
		},
		medium: {
			tries: 7,
			upper: 50,
		},
		hard: {
			tries: 5,
			upper: 100,
		}
	}

	state = {
		difficulty: null,
		lower: null,
		upper: null,
		maxTries: null,
		target: null,
		attempts: [],
		guess: null,
	};

	get attemptsRemaining() {
		return this.maxTries - this.attempts.length;
	}

	render() {
		return (
// this is sending all these values to View.js, and then destructured in line 12
				<View
					guess={this.state.guess}
					difficulty={this.state.difficulty}

					lower={this.state.lower}
					upper={this.state.upper}
					attemptsLeft={this.attemptsRemaining}

					onInitialise={({ target: { options, selectedIndex } }) => {
						this.initialise(options[selectedIndex].value);
					}}

					onGuessButton={() => {
						this.guess(parseInt(this.state.guess));
					}}

					onHelpButton={e => {
						this.help();
					}}

					onInput={({ target: { value: guess } }) => {
						this.setState({ guess });
					}}

				/>
		);
	}

	initialise(difficulty = "easy") {
			const {
				lower = 1,
				upper = 100,
				tries: maxTries = 10,
			} = App.DIFFICULTIES[difficulty];

		this.setState({
			difficulty,
			lower,
			upper,
			maxTries,
			target: randomInteger(lower, upper)

		})
	}

	guess(number = 1) {
		this.setState({
			guess: ""
		});

		if (this.attemptsRemaining === 0) {
			alert("Game over man! It's game over")

			this.setState({
				difficulty: null,
			});
		}

		const {
			target,
			upper,
			lower
		} = this.state;

		if (number === target) {
			alert("You win");

			this.setState({
				difficulty: null,
			})
		}

		if (number < lower || number > upper) {
			alert("Choose between");
		}		

		if (number < target) {
			alert("too low");
		}

		if (number > target) {
			alert("too high")
		}
	}

	help() {
		alert("Help");
	}


}

export default App;
