// the engine
//set up the App logic, methods etc

import { Component } from 'react';
import View from "./View.js";
// bring in View from another file
import { randomNumber, randomInteger } from './components/maths.js';



class App extends Component {
//use class if there is a need to handle state
	static DIFFICULTIES = {
		easy: {
			tries: 10,
			upper: 25
		},
		medium: {
			tries: 7,
			upper: 50,
		},
		hard: {
			tries: 5,
			upper: 100,
		}
	}

	state = {
		difficulty: null,
		lower: null,
		upper: null,
		maxTries: null,
		target: null,
		attempts: [],
	};

	render() {
		return (
				<View
					diificulty={this.state.difficulty}
					guess={() => {
						this.guess();
					}}

					help={e => {
						this.help();
					}}
				/>
		);
	}

	initialise(difficulty = "easy") {
//		1. grab difficulty setting from DIFFICULTIES
			const {
				lower = 1,
				upper = 100,
				tries: maxTries = 10,
			} = App.DIFFICULTIES[difficulty] || {};

		this.setState({
			difficulty,
			lower,
			upper,
			maxTries,
			target: randomInteger(lower, upper)

		})
	}

	guess(number = 1) {
		alert(number);
	}

	help() {
		alert("Help");
	}


}

export default App;
